import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  currentPage: 1,
  loading: true,
  hasMore: false,
  error: false,
  listPosts: [],
  loadingAddComment: false,
};

var findIndex = (arr, id) => {
  var result = -1;

  arr.forEach((item, i) => {
    if(item.id === id){
      result = i;
    }
  });

  return result;
}

export const posts = (state = initialState, action) => {
  switch (action.type) {
    case Types.FECTH_POSTS:
      return {
              ...state,
              listPosts: [],
              currentPage: 1,
              loading: true
            }
      break;
    case Types.RESPONSE_POSTS:
      state.listPosts = action.posts.data
      state.loading = false
      state.hasMore = action.posts.last_page > action.posts.current_page
      return {...state}
      break;
    case Types.SET_CURRENT_PAGE:
      state.currentPage = state.currentPage + 1
      return {...state}
      break;
    case Types.FECTH_MORE_POSTS:
      return {
              ...state,
              loading: true
            }
      break;
    case Types.RESPONSE_MORE_POSTS:
      state.listPosts = [...state.listPosts, ...action.posts.data]
      state.loading = false
      state.hasMore = action.posts.last_page > action.posts.current_page
      return {...state}
      break;
    case Types.REQUEST_ADD_COMMENT:
      return {...state, loadingAddComment: true}
      break;
    case Types.RESPONSE_ADD_COMMENT:
      return {...state, loadingAddComment:false}
      break;
    case Types.UPDATE_COMMENT_POST:
      var indexPost = findIndex(state.listPosts, action.payload.idPost);
      var post = state.listPosts.filter(post => post.id === action.payload.idPost)

      // const indexComment = findIndex(state.listPosts, idPost);
      console.log(indexPost, post);

      return {...state}
      break;
    default: return state
  }
}
