import React from 'react';
import {useForm} from 'react-hook-form';
import {RiErrorWarningLine} from "react-icons/ri";
import {Tooltip} from 'antd';

function ForgotPassword(){
  const {register, handleSubmit, errors} = useForm();

  const onSubmit = (data) => {
    console.log(data);
  }

  return(
    <React.Fragment>
      <div className="veri-code-container">
        <form onSubmit={handleSubmit(onSubmit)}>
          <h2>Quên mật khẩu</h2>
        <div className="veri-code-message"><span style={{color: 'black'}}>Sử dụng số điện thoại hoặc email mà bạn đã đăng kí để lấy lại mật khẩu</span></div>
          <div className="submit-error-veri-code"></div>
          <div className="form-veri-code-group">
            <input
              type="text"
              name="user_email"
              id="code"
              placeholder="Nhập tên tài khoản"
              ref={register()}
              style={errors.code ? {border: '1px solid red'} : null}
            />
          {errors.code && <Tooltip placement="right" title={<p className = "error_sex_tooltip" > Vui lòng nhập mã gồm 6 số</p>}><RiErrorWarningLine className="error_sex error_code"/></Tooltip>}
          </div>
          <div className="form-veri-code-group">
            <button type="submit">Xác nhận</button>
          </div>
        </form>
      </div>
    </React.Fragment>
  )
}

export default ForgotPassword;
