import React, { useState } from 'react';
import './UploadMuti.css';

function UploadMuti(){
  const [files, setFiles] = useState([]);

  const onChange = (e) => {
    if(e.target.files){
      //Tạo list image blob url
      const fileArr = Array.from(e.target.files).map((file) => URL.createObjectURL(file))
      setFiles((previewFile) => previewFile.concat(fileArr))
    }
  }

  const renderFile = source => {
    return source.map((photo, index) => {
      return <img src={photo} key={index}/>
    })
  }

  return(
    <div>
      <input type="file" multiple onChange={onChange} />
    <div className="list-preview-wrap">
        {renderFile(files)}
      </div>
    </div>
  )
}

export default UploadMuti;
