import React, {useEffect, useState, useRef, Suspense, useMemo} from 'react';
import './messenger.css';
import {BsSearch, BsFillCameraVideoFill, BsThreeDotsVertical, BsImage, BsPencilSquare} from 'react-icons/bs';
import {AiTwotoneSetting, AiTwotoneLike, AiFillFile, AiFillCamera, AiFillCloseCircle} from 'react-icons/ai';
import {Link, Route, Switch, useHistory} from 'react-router-dom';
import {Image,Transformation} from 'cloudinary-react';
import {ContentMessenger} from "./ContentMessenger.jsx";
import {Row} from 'antd';
import {useSelector, useDispatch} from 'react-redux';
import SiderMessenger from './SiderMessenger.jsx';
import moment from 'moment';
var socketIOClient = require('socket.io-client');

const socket = socketIOClient('http://localhost:8800/', {
  transports: ['websocket'],
});

function Messenger(props){
  const dispatch = useDispatch();
  const noChange = true;
  const currentUser = JSON.parse(localStorage.getItem('ustk')).info;
  moment.updateLocale('en', {
    relativeTime : {
        future: "%s",
        past:   "%s",
        s  : 'vài giây',
        ss : '%d phút',
        m:  "1 phút",
        mm: "%d phút",
        h:  "%d giờ",
        hh: "%d giờ",
        d:  "một ngày",
        dd: "%d ngày",
        w:  "một tuần",
        ww: "%d tuần",
        M:  "một tháng",
        MM: "%d tháng",
        y:  "một năm",
        yy: "%d năm"
    }
  });

  useEffect(() => {
    socket.on('connect', () => {
          console.log('connected mess');
    });

    socket.emit('register', currentUser.user_username);

    socket.on('connected_mess', (data) => {
          console.log(data);
    });
  },[]);

  const getMessageOneUser = (idUser) => {
    props.getMessageOneUser(idUser)
  }

  const ChatHello = () => {
    return(
      <div className="content-messenger-container content-hello">
        <p>MOHI</p>
        <p>Xin chào bạn</p>
        <p>Hãy nhắn tin cho bạn bè đi nào</p>
      </div>
    )
  }

  const chatHello = useMemo(() => {
    return ChatHello();
  }, [noChange]);

  return(
      <div className="messenger-container">
        <SiderMessenger />
        <Switch>
          <Route exact path="/messenger">
            {chatHello}
          </Route>
          <Suspense fallback={<div>loading</div>} >
          <Route path="/messenger/:idUser">
            <ContentMessenger getMessageOneUser={getMessageOneUser} />
          </Route>
          </Suspense>
        </Switch>
      </div>
  )
}

export default Messenger;
