import React, {useEffect, useState} from 'react';
import {useForm} from 'react-hook-form';
import {useHistory} from 'react-router-dom';

function LoginTwo(){
  const history = useHistory();
  const key = history.location.key;
  const {register, handleSubmit, errors} = useForm();
  const errorLogin = history.location.state ? history.location.state.error : '';
  const [error, setError] = useState(errorLogin);

  console.log(history);

  useEffect(() => {
    setError('')
  },[key])

  const onSubmit = (data) => {
    console.log(data);
  }

  return(
    <React.Fragment>
      <div className="login-two-container">
        <form onSubmit={handleSubmit(onSubmit)}>
          <h2>Đăng nhập</h2>
          <div className="form-login-two-group">
            <input type="text" name="code" id="code" placeholder="Tên đăng nhập" ref={register({required: true})}/>
          </div>
          <div className="form-login-two-group">
            <input type="text" name="code" id="code" placeholder="Mật khẩu" ref={register({required: true})}/>
          </div>
          <div className="login-two-message">{errorLogin}</div>
          <div className="form-login-two-group">
            <button type="submit">Xác nhận</button>
          </div>
        </form>
      </div>
    </React.Fragment>
  )
}

export default LoginTwo;
