import React, {useRef} from 'react';

function VideosPage(){
  const ref = useRef();
  const getCurrentTime = () => {
    capture(ref.current)
  }

  const capture = (video) => {
    const scaleFactor:number=0.25;
    let w = video.videoWidth * scaleFactor;
    let h = video.videoHeight * scaleFactor;
    let canvas = document.createElement('canvas');
    canvas.width = w;
    canvas.height = h;
    let ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, w, h);
    let dataURI = canvas.toDataURL('image/jpeg');
    // let a =
    let boj={
      canvas:canvas,
      file:this.dataURLtoFile(dataURI, `${+new Date()}.jpg`),
      base64:dataURI
    }
    console.log(boj);
    return boj;
  }

  return(
    <div className="filter-video-container">
      <div className="filter-video-title">
        <span>Video</span>
      </div>
      <div className="filter-video">
        <div className="filter-video-item">
          <video ref={ref} src="https://www.w3schools.com/TAgs/mov_bbb.ogg" poster="https://tse1.mm.bing.net/th?id=OIP.BoJfthnaI5oOTBP6GmrZrAHaEK&pid=Api&P=0&w=326&h=184src=" controls></video>
          <button onClick={getCurrentTime}>aaaaaaaaaa</button>
      </div>
      </div>
    </div>
  )
}

export default VideosPage;
