import React from 'react';
import {AiFillPicture} from 'react-icons/ai';
import {Link, Route, Switch} from 'react-router-dom';
import {FaCommentAlt} from 'react-icons/fa';
import {BsThreeDotsVertical} from 'react-icons/bs';
import './Notifications.css';
import moment from 'moment';
import {useSelector} from 'react-redux';
import {Spin, Tooltip} from 'antd';

function Notifications({notifycations}) {
  const {list, loading} = useSelector(state => state.notifycations);

  const removeNoti = (id) => {
      console.log(id);
  }

  moment.updateLocale('en', {
    relativeTime : {
        future: "%s",
        past:   "%s",
        s  : 'vài giây',
        ss : '%d phút',
        m:  "1 phút",
        mm: "%d phút",
        h:  "%d giờ",
        hh: "%d giờ",
        d:  "một ngày",
        dd: "%d ngày",
        w:  "một tuần",
        ww: "%d tuần",
        M:  "một tháng",
        MM: "%d tháng",
        y:  "một năm",
        yy: "%d năm"
    }
  });

  if (loading && !list.length) {
    return (<div className="noti-all">
      <div className="noti-container" style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          height: '100vh'
        }}>
        <Spin/>
      </div>
    </div>)
  }

  const Notifications = () => {
    return (<> < div className = "noti-all" > <div className="noti-container">
      <div className="noti-title">
        <h4>
          Thông báo của bạn</h4>
      </div>
      {list.map((noti, index) => {
        return(
          <div className="noti-main-content">
            <div className="noti-content-avatar">
              <img src={noti.notifications_send.user_avatar} alt={noti.notifications_send.user_first_name + ' ' + noti.notifications_send.user_last_name}/>
            </div>
            <Link to={"/post/" + noti.notification_PostIdFake} key={index} style={{color: 'black'}}>
            <div className="noti-content">
              <div className="noti-notification">
                <b style={{color: 'rgba(0,128,128'}} >{noti.notifications_send.user_first_name + ' ' + noti.notifications_send.user_last_name} </b>
                {noti.notification_Content}
                <b style={{color: 'rgba(0,128,128'}} > {noti.notifications_admin_post.user_first_name + ' ' + noti.notifications_admin_post.user_last_name}</b>
              </div>
              <div className="noti-status">
                  <FaCommentAlt/> {moment(noti.created_at, "YYYYMMDD\h:m:s").fromNow()}
              </div>
            </div>
          </Link>
            <div style={{padding: '10px 5px'}}>
              <Tooltip
                trigger={`click`}
                placement={`bottom`}
                title={
                  <div
                    onClick={() => removeNoti(noti.id)}
                    style={{
                      background: 'white',
                     padding: '5px',
                     boderRadius: '2px'
                   }}
                  >
                    Xóa thông báo
                  </div>
                }
              >
                <BsThreeDotsVertical/>
              </Tooltip>
            </div>
          </div>
        )
      })}
    </div>
  </div>
</>)
  }
  return (<Switch>
    <Route component={Notifications}/>
  </Switch>)
}
export default Notifications;
