import React from 'react';
import {useForm} from 'react-hook-form';
import {RiErrorWarningLine} from "react-icons/ri";
import {Tooltip} from 'antd';

function Register() {
  const {register, handleSubmit, errors} = useForm();
  const dates = [];
  const months = [];
  const years = [];

  const onSubmit = (data) => {
    console.log(data);
  }

  for (let i = 1; i <= 31; i++) {
    dates.push(<option key={i} value={i}>{i}</option>);
  }

  for (let i = 1; i <= 12; i++) {
    months.push(<option key={i} value={i}>{i}</option>);
  }

  for (let i = 0; i <= 60; i++) {
    const year = (new Date()).getFullYear() - i;
    years.push(<option key={i} value={year}>{year}</option>);
  }

  return (<div className="site-layout-register-container">
    <h2>Chào mừng bạn trở lại với Mohi</h2>
    <h4>Đăng kí</h4>
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="site-layout-register-group">
        <div className="register-first-name">
          <label htmlFor="register_first_name">Họ</label>
          <input id="register_first_name" name="register_first_name" type="text" placeholder="Họ..." ref={register({required: true})} style={errors.register_first_name
              ? {
                border: '1px solid red'
              }
              : null}/>
        </div>
        <div className="register-last-name">
          <label htmlFor="register_last_name">Tên</label>
          <input id="register_last_name" name="register_last_name" type="text" placeholder="Tên..." ref={register({required: true})} style={errors.register_last_name
              ? {
                border: '1px solid red'
              }
              : null}/>
        </div>
      </div>
      <div className="site-layout-register-group">
        <div className="register-email">
          <label htmlFor="register_email">Số điện thoại di động hoặc email</label>
          <input id="register_email" name="register_email" type="text" placeholder="Số điện thoại di động hoặc email..." ref={register({required: true})} style={errors.register_email
              ? {
                border: '1px solid red'
              }
              : null}/>
        </div>
      </div>
      <div className="site-layout-register-group">
        <div className="register-birthday">
          <label htmlFor="register_birthday">Ngày sinh</label>
          <select id="register_birthday_date" name="register_birthday_date" ref={register({required: true})}>
            {dates}
          </select>
          <select id="register_birthday_month" name="register_birthday_month" ref={register({required: true})}>
            {months}
          </select>
          <select id="register_birthday_year" name="register_birthday_year" ref={register({required: true})}>
            {years}
          </select>
        </div>
      </div>
      <div className="site-layout-register-group">
        <label htmlFor="register_sex">Giới tính</label>
        <div className="register-sex">
          <input id="register_sex_men" name="register_sex" value="nam" type="radio" ref={register({required: true})}/>
          <label htmlFor="register_sex_men">Nam</label>
        </div>
        <div className="register-sex">
          <input id="register_sex_women" name="register_sex" value="nữ" type="radio" ref={register({required: true})}/>
          <label htmlFor="register_sex_women">Nữ</label>
        </div>
        <div className="register-sex">
          <input id="register_sex_lt" name="register_sex" value="other" type="radio" ref={register({required: true})}/>
          <label htmlFor="register_sex_lt">Không xác định</label>
        </div>
        {errors.register_sex && <Tooltip placement="leftTop" title={"Vui lòng chọn giới tính"}><RiErrorWarningLine/></Tooltip>}
      </div>
      <div className="site-layout-register-group">
        <button>Đăng kí</button>
      </div>
    </form>
  </div>)
}

export default Register;
