import React, { useState} from 'react';
import './UploadMuti.css';
import { FiPlus } from "react-icons/fi";
import { AiOutlineClose } from "react-icons/ai";
import axios from 'axios';

function UploadMuti(props){
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);

  const onChange = (e) => {


      // const fileArr = Array.from(e.target.files).map((file) => {
        const formData = new FormData();
        formData.append('upload_preset', 'mohi_video');
        formData.append('file', e.target.files[0]);
        setLoading(true);

        axios.post('https://api.cloudinary.com/v1_1/mohi-vn/image/upload', formData)
        .then((res) => {
          console.log(res.data.secure_url);
          setFiles([...files, res.data.secure_url])
        })
        .then(setLoading(true))
        .catch((e) => {
          console.log(e);
        })
      // })
    //   setFiles((previewFile) => previewFile.concat(fileArr))URL.createObjectURL(file)

  }
  console.log(loading);

  const deleteFile = (index) => {
    const newFile = [...files]
    newFile.splice(index, 1)
    setFiles(newFile)
  }

  const renderFile = source => {
    return(
    <React.Fragment>
      {source.map((photo, index) => {
        return (
            <div className="list-preview-item" key={index}>
              <img src={photo} />
            <div className={"list-preview-item-presential "+index}><AiOutlineClose onClick={() => deleteFile(index)}/></div>
            </div>
        )
      })}
      {loading ? <div className="list-preview-next-item" >
        aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa
      </div> : null}
      <div className="list-preview-next-item" >
        <label htmlFor="create_post_upload_image"><FiPlus/></label>
      </div>
    </React.Fragment>
    )
  }

  return(
    <div className="list-preview-container">
      { props.showModalCreatePost ?
      <div className="list-preview-wrap">
        <div className="list-preview" >
          {renderFile(files)}
        </div>
      </div>
      : null}
        <input id="create_post_upload_image" type="file" multiple onChange={onChange} />
    </div>
  )
}

export default UploadMuti;
