import React, { useRef, useEffect } from 'react';
import {useSelector} from 'react-redux';
import Cropper from 'cropperjs';
import 'cropperjs/dist/cropper.css';
import {toggleStatusPresentialModal} from './../../Actions/index.jsx';

function ModalUpLoadAvatarProfile({presentialModal}){
  const {data} = useSelector(state => state.presentialModal);
  const refCropper = useRef();

  useEffect(() => {
    const current = refCropper.current;
    const cropper = new Cropper(current, {
      aspectRatio: 1,
      zoomable: false,
      scalable: false,
      crop(event) {
        console.log(event.detail.x);
        console.log(event.detail.y);
        console.log(event.detail.width);
        console.log(event.detail.height);
      },
    });
  }, [])

  console.log(data);

  return(
    <React.Fragment>
      <div className="modal-custom">
        <div className="image-cropper">
          <img src={data} ref={refCropper} alt="" />
        </div>
        <div className="action-cropper">
          <button >Lưu</button>
        <button onClick={() => toggleStatusPresentialModal()}>Hủy</button>
        </div>
      </div>
    </React.Fragment>
  )
}

export default ModalUpLoadAvatarProfile;
