import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  currentPage: 1,
  loading: true,
  hasMore: false,
  error: false,
  listPosts: []
};

export const posts = (state = initialState, action) => {
  switch (action.type) {
    case Types.SET_CURRENT_PAGE:
    console.log(action);
      state.currentPage = {...state.currentPage + 1}
      return {...state}
      break;
    case Types.POSTS:
      state.listPosts = action.posts
      state.loading = false
      console.log('has' + 2 > 1);
      state.hasMore = action.posts.last_page > action.posts.current_page
      return {...state}
      break;
    case Types.MORE_POSTS:
      state = action.posts
      return [...state, action.posts]
      break;
    default: return state
  }
}
