import React, {useState} from 'react';
import {FaUserAlt} from "react-icons/fa";
import {AiOutlinePlusCircle, AiFillEdit} from "react-icons/ai";
import {MdDelete} from "react-icons/md";
import {FcSettings} from "react-icons/fc";
import {Link, Route, Switch, useLocation, useParams} from 'react-router-dom';
import {Tooltip, Spin} from 'antd';
import {useDispatch} from 'react-redux';
import {toggleStatusPresentialModal} from './../../../Actions/index.jsx';
import {useSelector} from 'react-redux';

const OverView = (props) => {

  const dispatch = useDispatch();

  function editStudySpace() {
    dispatch(toggleStatusPresentialModal('modal_edit_study_space'));
  }

  function editLiveSpace() {
    dispatch(toggleStatusPresentialModal('modal_edit_live_space'));
  }

  function addWorkSpace() {
    dispatch(toggleStatusPresentialModal('modal_add_work_space'));
  }

  function addStudySpace() {
    dispatch(toggleStatusPresentialModal('modal_add_study_space'));
  }

  function addLiveSpace() {
    dispatch(toggleStatusPresentialModal('modal_add_live_space'));
  }

  function editWorkSpace() {
    dispatch(toggleStatusPresentialModal('modal_edit_work_space'));
  }

  function addWorkSpace() {
    dispatch(toggleStatusPresentialModal('modal_add_work_space'));
  }

  const onSubmitWorkSpace = (data) => {
    props.onSubmitWorkSpace(data);
  }

  const ButtonOptionWork = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editWorkSpace}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>
          Xa</span>
      </div>
    </React.Fragment>)

  }

  const ButtonOptionLive = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editLiveSpace}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>
          Xa</span>
      </div>
    </React.Fragment>)

  }

  const ButtonOptionStudy = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editStudySpace}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>Xa</span>
      </div>
    </React.Fragment>)

  }

  return (<div className="about-content-right-overview">
    <div className="about-content-right-first">
      <div className="about-content-right-first-place-work">
        {
          !props.info.user_workPlace && props.info.id === JSON.parse(localStorage.getItem('ustl')).info.id
            ? <div className="add-something">
                <div className="icon-add">
                  <AiOutlinePlusCircle/>
                </div>
                <div className="title-add" onClick={addWorkSpace}>
                  <span >Thm mt ni lm vic</span>
                </div>
              </div>
            : <div className="job-item">
                {props.info.user_workPlace}
                <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionWork}>
                  <span className="edit-work-space-button"><FcSettings/></span>
                </Tooltip>
              </div>
        }
      </div>
      <div className="about-content-right-first-place-study">
        {
          !props.info.user_studyPlace
            ? <div className="add-something">
                <div className="icon-add">
                  <AiOutlinePlusCircle/>
                </div>
                <div className="title-add" onClick={addStudySpace}>
                  <span >Thm mt trng cao ng/i hc</span>
                </div>
              </div>
            : <div className="collegs-item">
                Cao ng FPT
                <Tooltip trigger="click" placement="rightTop" title={ButtonOptionStudy}>
                  <span className="edit-work-space-button"><FcSettings/></span>
                </Tooltip>
              </div>
        }
      </div>
      <div className="about-content-right-first-place-live">
        {
          !props.info.user_currentPlace
            ? <div className="add-something">
                <div className="icon-add">
                  <AiOutlinePlusCircle/>
                </div>
                <div className="title-add" onClick={addLiveSpace}>
                  <span >Thm ni  hin ti</span>
                </div>
              </div>
            : <div className="job-item">
                {props.info.user_currentPlace}
                <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionLive}>
                  <span className="edit-work-space-button"><FcSettings/></span>
                </Tooltip>
              </div>
        }
      </div>
    </div>
    <div className="about-content-right-second">
      <ul>
        <li>
          {props.info.user_phone}
        </li>
        <li>
          {props.info.user_email}
        </li>
        <li>
          {props.info.user_birthday}
        </li>
        <li>
          <Link to={"/profile/" + props.idProfile + "/about/contact-info"}>
            <span>Chnh sa thng tin lin h v c bn</span>
          </Link>
        </li>
      </ul>
    </div>
  </div>)
}

const Education = (props) => {

  const dispatch = useDispatch();

  function addWorkSpace() {
    dispatch(toggleStatusPresentialModal('modal_add_work_space'));
  }
  function editWorkSpace() {
    dispatch(toggleStatusPresentialModal('modal_edit_work_space'));
  }
  function addStudySpace() {
    dispatch(toggleStatusPresentialModal('modal_add_study_space'));
  }
  function editStudySpace() {
    dispatch(toggleStatusPresentialModal('modal_edit_study_space'));
  }
  function addStudyHighSchool() {
    dispatch(toggleStatusPresentialModal('modal_add_study_high_school'));
  }
  function editStudyHighSchool() {
    dispatch(toggleStatusPresentialModal('modal_edit_study_high_school'));
  }

  const ButtonOptionWork = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editWorkSpace}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>
          Xa</span>
      </div>
    </React.Fragment>)

  }
  const ButtonOptionStudy = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editStudySpace}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>Xa</span>
      </div>
    </React.Fragment>)

  }

  const ButtonOptionStudyHighSchool = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editStudyHighSchool}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>Xa</span>
      </div>
    </React.Fragment>)

  }

  return (<> < div className = "about-content-education" > <div className="job">
    <h6>Cng vic</h6>
      {
        !props.info.user_workPlace
          ? <div className="add-something">
              <div className="icon-add">
                <AiOutlinePlusCircle/>
              </div>
              <div className="title-add" onClick={addWorkSpace}>
                <span >Thm mt ni lm vic</span>
              </div>
            </div>
          : <div className="job-item">
              {props.info.user_workPlace}
              <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionWork}>
                <span className="edit-work-space-button"><FcSettings/></span>
              </Tooltip>
            </div>
      }
  </div>
  <div className="collegs">
    <h6>Cao ng/i hc</h6>
      {
        !props.info.user_studyPlace
          ? <div className="add-something">
              <div className="icon-add">
                <AiOutlinePlusCircle/>
              </div>
              <div className="title-add" onClick={addStudySpace}>
                <span >Thm mt trng cao ng/i hc</span>
              </div>
            </div>
          : <div className="job-item">
              {props.info.user_studyPlace}
              <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionStudy}>
                <span className="edit-work-space-button"><FcSettings/></span>
              </Tooltip>
            </div>
      }
  </div>
  <div className="school">
    <h6>Trung hc</h6>
      {
        !props.info.user_high_school
          ? <div className="add-something">
              <div className="icon-add">
                <AiOutlinePlusCircle/>
              </div>
              <div className="title-add" onClick={addStudyHighSchool}>
                <span >Thm mt trng trung hc</span>
              </div>
            </div>
          : <div className="job-item">
              {props.info.user_high_school}
              <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionStudyHighSchool}>
                <span className="edit-work-space-button"><FcSettings/></span>
              </Tooltip>
            </div>
      }
  </div>
</div>
</>)
}

const ContactInfo = (props) => {
  const dispatch = useDispatch();

  function addEmail() {
    dispatch(toggleStatusPresentialModal('modal_add_email'));
  }
  function editEmail() {
    dispatch(toggleStatusPresentialModal('modal_edit_email'));
  }
  function addPhone() {
    dispatch(toggleStatusPresentialModal('modal_add_phone'));
  }
  function editPhone() {
    dispatch(toggleStatusPresentialModal('modal_edit_phone'));
  }
  function addWebsite() {
    dispatch(toggleStatusPresentialModal('modal_add_website'));
  }
  function editWebsite() {
    dispatch(toggleStatusPresentialModal('modal_edit_website'));
  }

  const ButtonOptionEmail = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editEmail}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>
          Xa</span>
      </div>
    </React.Fragment>)

  }

  const ButtonOptionPhone = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editPhone}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>
          Xa</span>
      </div>
    </React.Fragment>)

  }

  const ButtonOptionWebsite = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editWebsite}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>
          Xa</span>
      </div>
    </React.Fragment>)

  }

  return (<> < div className = "about-content-education" > <div className="job">
    <h6>Email</h6>
      {
        !props.info.user_email
          ? <div className="add-something">
              <div className="icon-add">
                <AiOutlinePlusCircle/>
              </div>
              <div className="title-add" onClick={addEmail}>
                <span >Thm mt email</span>
              </div>
            </div>
          : <div className="job-item">
              {props.info.user_email}
              <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionEmail}>
                <span className="edit-work-space-button"><FcSettings/></span>
              </Tooltip>
            </div>
      }
  </div>
  <div className="collegs">
    <h6>S in thoi</h6>
      {
        !props.info.user_phone
          ? <div className="add-something">
              <div className="icon-add">
                <AiOutlinePlusCircle/>
              </div>
              <div className="title-add" onClick={addPhone}>
                <span >Thm mt s in thoi</span>
              </div>
            </div>
          : <div className="job-item">
              {props.info.user_phone}
              <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionPhone}>
                <span className="edit-work-space-button"><FcSettings/></span>
              </Tooltip>
            </div>
      }
  </div>
  <div className="school">
    <h6>Website</h6>
      {
        !props.info.user_website
          ? <div className="add-something">
              <div className="icon-add">
                <AiOutlinePlusCircle/>
              </div>
              <div className="title-add" onClick={addWebsite}>
                <span >Thm mt link website</span>
              </div>
            </div>
          : <div className="job-item">
              {props.info.user_website}
              <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionWebsite}>
                <span className="edit-work-space-button"><FcSettings/></span>
              </Tooltip>
            </div>
      }
  </div>
</div>
</>)
}

const Bio = (props) => {

  const dispatch = useDispatch();

  function addIntroduce() {
    dispatch(toggleStatusPresentialModal('modal_add_introduce'));
  }
  function editIntroduce() {
    dispatch(toggleStatusPresentialModal('modal_edit_introduce'));
  }
  function addNickName() {
    dispatch(toggleStatusPresentialModal('modal_add_nick_name'));
  }
  function editNickName() {
    dispatch(toggleStatusPresentialModal('modal_edit_nick_name'));
  }

  const ButtonOptionIntroduce = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editIntroduce}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>
          Xa</span>
      </div>
    </React.Fragment>)

  }

  const ButtonOptionNickName = () => {

    return (<React.Fragment>
      <div className="button-option">
        <span onClick={editNickName}><AiFillEdit/>
          Chnh sa</span>
        <span><MdDelete/>
          Xa</span>
      </div>
    </React.Fragment>)

  }
  return (<> < div className = "about-content-education" > <div className="job">
    <h6>Gii thiu v bn thn</h6>
      {
        !props.info.user_introduce
          ? <div className="add-something">
              <div className="icon-add">
                <AiOutlinePlusCircle/>
              </div>
              <div className="title-add" onClick={addIntroduce}>
                <span >Vit vi iu v bn thn bn</span>
              </div>
            </div>
          : <div className="job-item">
              {props.info.user_introduce}
              <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionIntroduce}>
                <span className="edit-work-space-button"><FcSettings/></span>
              </Tooltip>
            </div>
      }
  </div>
  <div className="collegs">
    <h6>Cc tn khc</h6>
      {
        !props.info.user_nick_name
          ? <div className="add-something">
              <div className="icon-add">
                <AiOutlinePlusCircle/>
              </div>
              <div className="title-add" onClick={addNickName}>
                <span >Thm mt bit danh hoc tn khai sinh ca bn</span>
              </div>
            </div>
          : <div className="job-item">
              {props.info.user_nick_name}
              <span>
                <small>(Hin th  u trang c nhn)</small>
              </span>
              <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionNickName}>
                <span className="edit-work-space-button"><FcSettings/></span>
              </Tooltip>
            </div>
      }
  </div>
</div>
</>)
}

function About({profile}) {
  const {idProfile} = useParams();
  const path = useLocation().pathname.split('/');
  const isActive = path[4];
  const {loading, info} = useSelector(state => state.profile);

  if (loading && !info) {
    return (<div className="about-container" style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '300px'
      }}>
      <Spin/>
    </div>)
  }
  return (<> < div className = "about-container" > <div className="about-header">
    <FaUserAlt/>
    <h5>Gii thiu</h5>
  </div>
  <div className="about-content">
    <div className="about-content-left">
      <ul>
        <Link to={"/profile/" + idProfile + "/about/overview"}>
          <li className={isActive === 'overview' || !isActive
              ? 'active'
              : ''}>
            <span>Tng quan</span>
          </li>
        </Link>
        <Link to={"/profile/" + idProfile + "/about/education"}>
          <li className={isActive === 'education'
              ? 'active'
              : ''}>
            <span>Cng vic v hc vn</span>
          </li>
        </Link>
        <Link to={"/profile/" + idProfile + "/about/contact-info"}>
          <li className={isActive === 'contact-info'
              ? 'active'
              : ''}>
            <span>Thng tin lin h v c bn</span>
          </li>
        </Link>
        <Link to={"/profile/" + idProfile + "/about/bio"}>
          <li className={isActive === 'bio'
              ? 'active'
              : ''}>
            <span>Chi tit v bn</span>
          </li>
        </Link>
      </ul>
    </div>
    <div className="about-content-right">
      <Switch>
        <Route path="/profile/:idProfile/about/overview"><OverView idProfile={idProfile} info={info}/></Route>
        <Route path="/profile/:idProfile/about/education"><Education info={info}/></Route>
        <Route path="/profile/:idProfile/about/contact-info"><ContactInfo info={info}/></Route>
        <Route path="/profile/:idProfile/about/bio"><Bio info={info}/></Route>
        <Route><OverView idProfile={idProfile} info={info}/></Route>
      </Switch>
    </div>
  </div> < /div>
</ >)
}

export default About;
