import React, {useState, useRef, useEffect} from 'react';
import {Editor, EditorState, convertToRaw } from 'draft-js';
import style from 'styled-components';
import './HomePage.css';
import { FaUserTag, FaImage, FaUserFriends, FaLock} from "react-icons/fa";
import {MdTagFaces, MdPublic} from "react-icons/md";
import {Select, Switch, Button, Tooltip} from 'antd';
import Picker from 'emoji-picker-react';
import draftToHtml from 'draftjs-to-html';

const StyleEditor = style.div`
  width: 510px;
  max-height: 200px;
  overflow-y: auto;
  margin-left: 10px;
  font-size: 14px;
`;

function HomePage(props){

  const [editorState, setEditorState] = useState(() =>
    EditorState.createEmpty(),
  );
  const [valueEditor, setValueEditor] = useState();
  const createPostInputRef = useRef();
  const {Option} = Select;
  const [isLoadingSubmit, setIsLoadingSubmit] = useState(false);
  const [chosenEmoji, setChosenEmoji] = useState(null);

  useEffect(() => {
    if(props.showModalCreatePost){
      let createPostInput = createPostInputRef.current;
      createPostInput.focus()
    }
    if(!props.showModalCreatePost){
      setEditorState(editorState)
    }

  },[props.showModalCreatePost])

  function showModalCreatePostFunc(){
    props.showModalCreatePostFunc()
  }

  function submitCreatePost(){
    setIsLoadingSubmit(true)
  }

  const onEmojiClick = (event, emojiObject) => {
    setChosenEmoji(emojiObject)

    currentContent.create(
      DraftEntityType,
      DraftEntityMutability,
      emojiObject.emoji
    )

  };

  const onChangeEditor = (editorState) => {
    const currentContent = editorState.getCurrentContent();
    const contentRaw = convertToRaw(currentContent);
    const value = currentContent.hasText() ? draftToHtml(contentRaw) : "";
    setValueEditor(currentContent);
    setEditorState(editorState)
    console.log(valueEditor);
  }

  const EmojiPicker = () => {
    return(
      <div className="list-emoji">
        <Picker  onEmojiClick={onEmojiClick} disableSearchBar/>
      </div>
    )
  }
  return(
    <div>
    <div className="page-home-container">
      <div className="page-left">
        <div className="create-post" onClick={() => showModalCreatePostFunc()} style={props.showModalCreatePost ? {zIndex: '10'} : null}>
          <div className="create-post-input">
            <div className="create-post-avatar">
              <img src="http://xemanhdep.com/wp-content/uploads/2016/04/hinh-anh-girl-xinh-dep-nhat-2016-10.jpg" alt="" />
            </div>
            <div className="create-post-editor">
              <StyleEditor>
                <Editor ref={createPostInputRef} editorState={editorState} onChange={onChangeEditor} placeholder="Viết bài dì đó..."/>
              </StyleEditor>
              {props.showModalCreatePost ?
                <div className="create-post-reaction" style={{textAlign: 'right'}}>
                  <Tooltip trigger={['click']} title={EmojiPicker} >
                      <button className="icon-choose-emoji-picker"><MdTagFaces style={{fontSize: '20px', fill: 'rgba(0,0,0,0.5)', cursor: 'pointer'}}/></button>
                  </Tooltip>
              </div>
              :null}
            </div>
          </div>
          <div className="create-post-option">
            <div className="create-post-choose-image">
              <label htmlFor="choose-image">
                <FaImage style={{ fontSize: "20px", marginRight: '7px' }} />
                Ảnh/video
              </label>
              <input type="file" id="choose-image" />
            </div>
            <div className="create-post-tag">
              <FaUserTag style={{ fontSize: "20px", marginRight: '7px' }} />
              Gắn thẻ
            </div>
          </div>
          {props.showModalCreatePost ?
            <div>
              <div className="create-post-choose-place-post">
                <div className="create-post-newfeed-place">
                  <div>
                      <Switch></Switch>
                      <span className="place-post">Tin của bạn</span>
                  </div>
                  <Select className="select-privacy" defaultValue="public" style={{ width: 140 }} loading>
                      <Option value="public"><MdPublic /> Công khai</Option>
                      <Option value="friend"><FaUserFriends /> Bạn bè</Option>
                      <Option value="onlyme"><FaLock /> Chỉ mình tôi</Option>
                  </Select>
                </div>
                <div className="create-post-story-place">
                  <div>
                      <Switch defaultChecked></Switch>
                      <span className="place-post">Bảng tin</span>
                  </div>
                  <Select className="select-privacy" defaultValue="public" style={{ width: 140 }} loading>
                      <Option value="public"><MdPublic /> Công khai</Option>
                      <Option value="friend"><FaUserFriends /> Bạn bè</Option>
                      <Option value="onlyme"><FaLock /> Chỉ mình tôi</Option>
                  </Select>
                </div>
              </div>
              <div className="create-post-submit">
                {isLoadingSubmit ?
                  <Button loading>Đăng</Button>
                :
                  <Button onClick={() => submitCreatePost()} >Đăng</Button>
                }
              </div>
            </div>
          : null}
        </div>
      </div>
      <div className="page-right">
        right{valueEditor}
      </div>
    </div>
    </div>
  )
}

export default HomePage;
