import React, {useEffect} from 'react';
import Profile from './../Components/Profiles/MyProfile/Index.jsx';
import {useDispatch, useSelector} from 'react-redux';
import axios from 'axios';
import {useParams} from 'react-router-dom';
import {requestPostsProfile} from './../Actions/index.jsx';
import {responsePostsProfile} from './../Actions/index.jsx';
import {responseMorePostsProfile} from './../Actions/index.jsx';
import {fetchMorePostsProfile} from './../Actions/index.jsx';

function ProfileContainer({myPosts}){
  const dispatch = useDispatch();
  const {idProfile} = useParams();
  const { currentPage } = useSelector(state => state.myPosts);

  const fetchPostsProfile = () => {
    dispatch(requestPostsProfile())

    axios.get('/api/auth/post-profile/'+ idProfile)
    .then((res) => {
      dispatch(responsePostsProfile(res.data))
    })
  }

  useEffect(() => {
    fetchPostsProfile()
  }, [])

  useEffect(() => {
    if(currentPage > 1){
      dispatch(fetchMorePostsProfile())
      getPostNewFeedInfinite();
    }
  }, [currentPage])

  const getPostNewFeedInfinite = () => {
        axios({
          method: 'GET',
          url: '/api/auth/post-profile/'+ idProfile,
          params: {page : currentPage}
        })
        .then(res =>{
          dispatch(responseMorePostsProfile(res.data))
        })
        .catch(e => {
          if(axios.isCancel(e)) return
        })
  }

  const sendReactionPost = async (reaction, postId) => {
    if(reaction === 'unlike'){
      console.log(reaction, postId);
    }else {
      await axios.post('/api/auth/create-action', {actions_PostId: postId, actions_action: reaction}).then((res) => {
      }).catch((e) => {
      })
    }
  }

  const onSubmitComment = async (value, idPost) => {
    dispatch(requestAddComment())

    await axios.post('/api/auth/create-comment', {comment_PostId: idPost, comment_Content: value, comment_Type: 'parent'})
    .then((res) => {
      dispatch(responseAddComment(res.data))
    }).catch((e) => {
    })
  }

  return(
    <Profile sendReactionPost={sendReactionPost} onSubmitComment={onSubmitComment}/>
  )
}

export default ProfileContainer;
