import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  status: false
};

export const spin = (state = initialState, action) => {
  console.log(state);
  switch (action.type) {
    case Types.TOGGLE_SPIN:
      state = action.status
      return state
      break;
    default: return state
  }
}
