import React, {useState} from 'react';
import Login from './../app/Authen/Login.jsx';
import LoginTwo from './../app/Authen/LoginTwo.jsx';
import axios from 'axios';
import {API_LOGIN} from './../Constants/Api.jsx';
import {useHistory} from 'react-router-dom';
import {Route} from 'react-router-dom';

export default function LoginContainer(props){

  const history = useHistory();
  console.log(history);
  const setLoginFunc = (data) => {
    axios.post('/api/auth/login', data).then((res) => {
      props.setLoginFunc(res)
    }).catch((err) => {
      history.push('/login', err.response.data)
    });
  }

  return(
    <>
      <Route exact path="/">
        <Login setLoginFunc={setLoginFunc}/>
      </Route>
      <Route path="/login">
        <LoginTwo />
      </Route>
  </>
  )
}
