import React, {useState, useEffect, useRef} from 'react';
import {Link} from 'react-router-dom';
import'./PopupEditPage.css';
import L from "leaflet";
import * as ELG from "esri-leaflet-geocoder";
import "leaflet/dist/leaflet.css";
import "esri-leaflet-geocoder/dist/esri-leaflet-geocoder.css";
import {PUBLIC_URL} from './../../Constants/public.jsx';

function PopupEditPage(){
  const [lat, setLat] = useState(10.7553411);
  const [long, setLong] = useState(106.4150395);
  const [zoom, setZoom] = useState(5);
  const mapRef = useRef();

  useEffect(() => {


        var map             = L.map('map_page', {center: [51.505, -0.09], zoom: 13}),
        marker          = L.marker(map.getCenter()).addTo(map),
        onLocationfound = function(e){
          marker.setLatLng(e.latlng);
          map.setView(marker.getLatLng(),map.getZoom());
          alert('Marker has been set to position :'+marker.getLatLng().toString());
        };

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);

    map.on('locationfound', onLocationfound);
    map.locate();
  }, []);

  const getMyAdress = () => {
    navigator.geolocation.getCurrentPosition(getLatLon);
  }

  function getLatLon(position) {

    let latitude = position.coords.latitude;
    let longitude = position.coords.longitude;

    // const results = new L.LayerGroup().addTo(map);
    // results.addLayer(L.marker({lat:latitude, lng:longitude}));
  }
  return(
    <div className="popup-edit-page">
      <div className="popup-edit-page-container">
        <div className="map-page-container">
          <h5>Chỉnh sửa vị trí cửa hàng <Link to="">ReactJS Việt Nam</Link></h5>
          <div id="map_page" className="map_page" ref={mapRef}></div>
          <div className="map-page-button">
            <button type="submit" onClick={getMyAdress}>Lấy vị trí của bạn</button>
            <button type="submit">Xác nhận</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default PopupEditPage;
